import{_ as e,o as t,c as r,Q as o}from"./chunks/framework.25ff06b1.js";const k=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"可选参数","slug":"可选参数","link":"#可选参数","children":[{"level":3,"title":"使用示例","slug":"使用示例","link":"#使用示例","children":[]}]}],"relativePath":"article/backend/gitLearn/常用命令/4.reset.md","filePath":"article/backend/gitLearn/常用命令/4.reset.md","lastUpdated":1707839101000}'),s={name:"article/backend/gitLearn/常用命令/4.reset.md"},n=o('<blockquote><p><code>git reset</code>用于恢复到历史提交点，重置暂存区与工作目录的内容</p></blockquote><h2 id="可选参数" tabindex="-1">可选参数 <a class="header-anchor" href="#可选参数" aria-label="Permalink to &quot;可选参数&quot;">​</a></h2><p>reset 有三个选项可使用</p><ol><li><strong>--hard</strong> 重置位置的同时，直接将 <strong>working Tree 工作目录</strong>、 <strong>index 暂存区</strong>及 <strong>repository</strong> 都重置成目标<strong>Reset</strong>节点的內容</li><li><strong>--soft</strong> 重置位置的同时，保留<strong>working Tree 工作目录</strong>和<strong>index 暂存区</strong>的内容，只让<strong>repository</strong>中的内容和 <strong>reset</strong> 目标节点保持一致</li><li><strong>--mixed（默认）</strong> 重置位置的同时，只保留<strong>Working Tree 工作目录</strong>的內容，但会将 <strong>Index 暂存区</strong> 和 <strong>Repository</strong> 中的內容更改和 reset 目标节点一致</li></ol><h3 id="使用示例" tabindex="-1">使用示例 <a class="header-anchor" href="#使用示例" aria-label="Permalink to &quot;使用示例&quot;">​</a></h3><ol><li><code>git reset</code> 将 add 到暂存区的内容回退</li><li><code>git reset --hard b7b73147ca8d6fc20e451d7b36</code> 恢复到指定提交版本（先通过 git log 查看版本号)，重置 stage 区和工作目录里的内容。</li><li><code>git reset --hard HEAD^^^</code> 恢复前三个版本</li><li><code>git reset --soft</code> 保留工作区的内容，只回退 commit 的动作。保留 <strong>working tree 工作目录</strong>的內容，<strong>index 暂存区</strong>与 <strong>working tree</strong> 工作目录的內容一致，只是仓库<strong>repository</strong> 中的內容的改变。</li><li><code>git reset HEAD -- .</code> 撤销暂存区的文件</li><li><code>git reset --hard</code> 清空工作区和暂存区的改动</li><li><code>git reset HEAD hd.js</code> 放弃已经 add 暂存区的文件 hd.js</li></ol>',6),i=[n];function a(l,d,g,c,_,h){return t(),r("div",null,i)}const m=e(s,[["render",a]]);export{k as __pageData,m as default};
