import{_ as s,o as a,c as e,Q as l}from"./chunks/framework.ab7e7ee0.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"article/bug/服务器/4-mysql.md","filePath":"article/bug/服务器/4-mysql.md","lastUpdated":1707839101000}'),n={name:"article/bug/服务器/4-mysql.md"},p=l(`<p>由于很长时间没有使用MySql，今天想启动一下发现启动不了，输入MySql启动命令报错。 操作步骤如下： 输入MySql启动命令：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">sudo /usr/local/mysql/support-files/mysql.server start</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo /usr/local/mysql/support-files/mysql.server start</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>报错信息如下：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">Starting MySQL</span></span>
<span class="line"><span style="color:#e1e4e8;">.Logging to &#39;/usr/local/mysql/data/mac.local.err&#39;.</span></span>
<span class="line"><span style="color:#e1e4e8;"> ERROR! The server quit without updating PID file (/usr/local/mysql/data/mac.local.pid).</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">Starting MySQL</span></span>
<span class="line"><span style="color:#24292e;">.Logging to &#39;/usr/local/mysql/data/mac.local.err&#39;.</span></span>
<span class="line"><span style="color:#24292e;"> ERROR! The server quit without updating PID file (/usr/local/mysql/data/mac.local.pid).</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>后面找到原因发现目录<code>/usr/local/mysql/data/</code>的mysql拥有者权限发生了变更，没有写的权限，所以需要对该目录赋予写的权限，操作命令如下：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">sudo chown -R mysql /usr/local/mysql/data</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo chown -R mysql /usr/local/mysql/data</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>然后再次输入启动命令：</p><div class="language-text vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#e1e4e8;">sudo /usr/local/mysql/support-files/mysql.server start</span></span>
<span class="line"><span style="color:#e1e4e8;">Starting MySQL</span></span>
<span class="line"><span style="color:#e1e4e8;">.Logging to &#39;/usr/local/mysql/data/oxy-mac.local.err&#39;.</span></span>
<span class="line"><span style="color:#e1e4e8;">. SUCCESS!</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#24292e;">sudo /usr/local/mysql/support-files/mysql.server start</span></span>
<span class="line"><span style="color:#24292e;">Starting MySQL</span></span>
<span class="line"><span style="color:#24292e;">.Logging to &#39;/usr/local/mysql/data/oxy-mac.local.err&#39;.</span></span>
<span class="line"><span style="color:#24292e;">. SUCCESS!</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><p>成功了！</p>`,9),t=[p];function o(r,c,i,d,u,m){return a(),e("div",null,t)}const b=s(n,[["render",o]]);export{g as __pageData,b as default};
